mapper.select:
SELECT idx_report, `title`, idx_project_job_projectid
    , IF(d_count_total!=d_count,0,1) AS reportStatus
    FROM KT_REPORT WHERE idx_project_job_projectid=#{idx_project_job_projectid}

mapper.update:
UPDATE KT_REPORT
    SET idx=#{idx_user}

    , title=#{title}


    , summary0=#{summary0}

    WHERE idx_report=#{idx_report}

UPDATE KT_PROJECT
    SET project_type=#{project_type},
    project_status=#{project_status}
    WHERE idx_project=#{idx_project}

mapper.select:
SELECT COUNT(1)
FROM KT_REPORT_FILTER_METADATA
WHERE idx_report=#{idx_report} AND #{speaker} AND #{chapter}

mapper.update:
UPDATE KT_REPORT_FILTER_METADATA
SET answer_cnt= answer_cnt+1,
    word_length = word_length+#{length}
WHERE idx_report=#{idx_report} AND #{speaker} AND #{chapter}

UPDATE KT_REPORT
SET create_dt = create_dt

    , title=#{title}


    , summary0=#{summary0}

WHERE idx_report=#{idx_report}

UPDATE KT_REPORT_DATA
SET summary0=#{summary_md}
WHERE idx_report_data=#{idx_data} AND idx_user=#{idx}

mapper.select:
SELECT count(1)
FROM KT_REPORT_DATA
WHERE idx_report_data=#{idx_report_data} AND idx_user=#{idx_user}

mapper.update:
UPDATE KT_REPORT
    SET d_count = d_count + 1
    WHERE idx_report=#{idx_report}
