mapper.select:
SELECT * FROM KT_USER WHERE 

    idx_user = #{idx_user}


    user_id = #{user_id}

mapper.update:
UPDATE KT_USER SET
    user_name=#{user_name}

    , user_pw=#{user_pw}


    , user_name=#{user_name}


    , user_phone=#{user_phone}


    , user_type=#{user_type}


    , user_status=#{user_status}

    WHERE idx_user=#{idx_user}
